import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [location, setLocation] = useState(null);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  const fetchLocation = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/api/get-location/?phone=${phoneNumber}`);
      setLocation(response.data);
      setError('');
    } catch (err) {
      setLocation(null);
      setError('Error fetching location. Make sure the phone number is valid.');
    }
  };

  return (
    <div className="App">
      <h1>Phone Number Location Finder</h1>
      <input
        type="text"
        placeholder="Enter phone number"
        value={phoneNumber}
        onChange={handleInputChange}
      />
      <button onClick={fetchLocation}>Get Location</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {location && (
        <div>
          <p><strong>Country:</strong> {location.country}</p>
          <p><strong>Location:</strong> {location.location}</p>
          <p><strong>Carrier:</strong> {location.carrier}</p>
        </div>
      )}
    </div>
  );
}

export default App;
